import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { ButtonToolbar, Button, Card } from 'react-bootstrap';
import axios from 'axios';
import NavBar from './NavBar'
import OrderList from './OrderList';

class MainPage extends Component {

  constructor() {
    super();

    this.state = {
      orders : []
    }

  }

  componentDidMount () {
    var url = "http://localhost:3000/ordersByStatus/open";
    this.getData(url)

  }

  getData (url) {
    axios.get(url)
    .then(response => {
        if(response.status === 200){
            this.setState({ orders: response.data})
            console.log(this.state.orders)
        }
    })
    .catch(error => {
        console.log(error);
    })
  }

  handleClick(event) {
        var url = null;
        if(event === "open"){
            url = "http://localhost:3000/ordersByStatus/open";
        }

        if(event === "closed"){
            url = "http://localhost:3000/ordersByStatus/closed";
        }

        if(event === "all"){
            url = "http://localhost:3000/allOrders";
        }

        this.getData(url)
  }

  render() {
    return (
      <div>
        <NavBar/>

        <Card>
            <Card.Header>
                <ButtonToolbar>
                    <Button variant="outline-success" onClick={event => this.handleClick("open")} >Open</Button>
                    <Button variant="outline-danger" onClick={event => this.handleClick("closed")} >Closed</Button>
                    <Button variant="outline-dark" onClick={event => this.handleClick("all")} >All</Button>
                </ButtonToolbar>
            </Card.Header>
            <Card.Body>
                <OrderList orders={this.state.orders} /> 
            </Card.Body>
        </Card>

        {/* <div>
            <ButtonToolbar>
                <Button variant="outline-success">Open</Button>
                <Button variant="outline-danger">Closed</Button>
                <Button variant="outline-dark">All</Button>
            </ButtonToolbar>
            <OrderList orders={this.state.orders} />    
        </div> */}
      </div>
    );
  }
}

export default withRouter(MainPage);
