import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';

class DeleteModal extends Component {

    constructor(props){
        super(props)

        this.state= {
            orderId : this.props.item.orderId,
            itemId: this.props.item.itemId,
            itemRefId: this.props.item.itemRefId,
            itemQuantity: this.props.item.itemQuantity
        }

    }

    handleDelete(event) {
        var url = "http://localhost:3000/removeItem/"+this.state.orderId+"/"+this.state.itemId

        console.log("### Quantity",this.state.itemQuantity)

        axios.put(url,{
            refId: this.state.itemRefId,
            quantity: this.state.itemQuantity
        })
        .then(response => {
            if(response.status === 200){
                // console.log("data 1", response.data[0])
                this.props.removeItem(response.data)
            }
        })
        .catch(error => {
            console.log(error);
        })

        this.props.onHide();
    }


    render() {

        return (
            <Modal {...this.props}>
            <Modal.Header closeButton>
              <Modal.Title>Delete Item</Modal.Title>
            </Modal.Header>
            <Modal.Body>Are you sure you wish to delete this item? </Modal.Body>
            <Modal.Footer>
              <Button variant="outline-danger" onClick={this.props.onHide}>Cancel</Button>
              <Button variant="outline-success" onClick={event => this.handleDelete(event)}>Ok</Button>
            </Modal.Footer>
          </Modal>
        );
    }
}

export default withRouter(DeleteModal);
