import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';

class DeleteModal extends Component {

    // constructor(props){
    //     super(props)

    //     this.state= {}

    // }

    handleDelete(event) {

        var url = "http://localhost:3000/removeItem/"+this.props.ord;

        axios.delete(url)
            .then(response => {
                if(response.status === 200){
                    console.log(response.data)
                    this.setState({ items: response.data.items})
                    console.log(this.state.items)
                }
            })
            .catch(error => {
                console.log(error);
            })
    }


    render() {
        return (
            <Modal {...this.props}>
            <Modal.Header closeButton>
              <Modal.Title>Delete Item</Modal.Title>
            </Modal.Header>
            <Modal.Body>Are you sure you wish to delete this item? </Modal.Body>
            <Modal.Footer>
              <Button variant="outline-danger" onClick={this.props.onHide}>Cancel</Button>
              <Button variant="outline-success" onClick={event => this.handleDelete(event)}>Ok</Button>
            </Modal.Footer>
          </Modal>
        );
    }
}

export default withRouter(DeleteModal);
