import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from 'axios'
import NavBar from './NavBar'
import '../App.css'
import ItemList from './ItemList';
import AllItems from './AllItems';

class OrderItems extends Component {

    constructor(props) {
        super(props)

        this.state = {
            orderId: this.props.match.params.OrderId,
            items : [],
            allItems: []
        }
    }

    componentDidMount () {
        var url = "http://localhost:3000/itemsByOrderId/"+this.state.orderId;
        axios.get(url)
        .then(response => {
            if(response.status === 200){
                console.log(response.data)
                this.setState({ items: response.data.items})
                console.log(this.state.items)
            }
        })
        .catch(error => {
            console.log(error);
        })
    }

    handleClick(event) {
        this.props.history.push("/MainPage")
    }

    render() {
        return (
        <div className="order-items">
            <NavBar/>
            <Container fluid='true'>
                <Row>

                <Col md={4}>
                        <Card>
                            <Card.Header>Item List</Card.Header>
                            <Card.Body>
                                <AllItems orderId={this.state.orderId} />
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col md={8}>
                        <Card>
                            <Card.Header>
                                <Button variant="outline-primary" onClick={(event) => this.handleClick(event)}><i className="fa fa-chevron-left"></i></Button>
                            </Card.Header>
                            <Card.Body>
                                <ItemList items={this.state.items} orderId={this.state.orderId}/>
                            </Card.Body>
                            <Card.Footer>
                                <h5 style={{color: '#5cb85c', textAlign:'right'}}>Total Price: $34</h5>
                            </Card.Footer>
                        </Card>
                    </Col>
                </Row>
            </Container>

        </div>
        );  
    }
}

export default withRouter(OrderItems);
