import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from 'axios'
import NavBar from './NavBar'
import '../App.css'
import ItemList from './ItemList';
import AddItems from './AddItems';

class OrderItems extends Component {

    constructor(props) {
        super(props)

        this.state = {
            orderId: this.props.match.params.OrderId,
            items : [],
            modalShow: false
        }
    }

    componentDidMount () {

        var url = "http://localhost:3000/itemsByOrderId/"+this.state.orderId;
        // var url = "http://localhost:3000/order/"+this.state.orderId;

        axios.get(url)
            .then(response => {
                if(response.status === 200){
                    console.log(response.data)
                    this.setState({ items: response.data.items})
                    console.log(this.state.items)
                }
            })
            .catch(error => {
                console.log(error);
            })

      }

    render() {
        let modalClose = () => this.setState({ modalShow: false });
        return (
        <div className="order-items">
            <NavBar/>
            <Container>
                <Row>
                    <Col>
                        <Card>
                            <Card.Header>
                                <Button variant="outline-primary"><i className="fa fa-chevron-left"></i></Button>
                                <Button className="pull-right" variant="outline-success" onClick={() => this.setState({ modalShow: true })}>Add Items</Button>
                            </Card.Header>
                            <Card.Body>
                                <ItemList items={this.state.items} orderId={this.state.orderId}/>
                            </Card.Body>
                            <Card.Footer>
                                <h5 style={{color: '#5cb85c', textAlign:'right'}}>Total Price: $34</h5>
                            </Card.Footer>
                        </Card>
                    </Col>
                </Row>
            </Container>

            <AddItems show={this.state.modalShow} onHide={modalClose}/>
        </div>
        );  
    }
}

export default withRouter(OrderItems);
