import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Table, Badge, Overlay, Tooltip } from 'react-bootstrap';
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css'
var empty = require('is-empty');

class AllItems extends Component {

  constructor(props) {
    super(props)

    this.state = {
        orderId: this.props.orderId,
        allItems: this.props.allItems,
        showTooltip: "false"
    }
    this.attachRef = target => this.setState({ target });
  }

handleRowClick(event, item) {
    const selectedItem = this.props.orderedItems.filter(element => element.id._id === item._id);

    if(!empty(selectedItem)){
        this.setState({showTooltip: "true"})
    }

    if (item.quantity !== 0 && empty(selectedItem)){

        var url = "http://localhost:3000/addNewItem/"+this.state.orderId;

        axios.put(url,{
            id:item._id,
            quantity: 1
        })
        .then(response => {
            if(response.status === 200){
                this.props.handleChanges(response.data)
            }
        })
        .catch(error => {
            console.log(error);
        })
    }
}



  render() {
    const { target } = this.state;
    return (
      <div>

         <Container>
            <Row>
            <Table responsive size="sm" hover>
                    <thead>
                        <tr>
                            <th>Item</th>
                            <th>Unit Price</th>
                            <th>Available Quantity</th>
                            <th></th>
                        </tr>
                    </thead>

                    {this.props.allItems.map((item) => (
                    <tbody key={item._id}> 

                        <tr onClick={(event) => this.handleRowClick(event,item)} ref={this.attachRef}>
                            {/* <Overlay show={this.state.showTooltip} placement="top" container={this}>
                                {props => (
                                    <Tooltip>Already Added !!!</Tooltip>
                                )}
                            </Overlay> */}
                             <Overlay target={target} show={this.state.showTooltip} placement="auto">
                                {({ placement, scheduleUpdate, arrowProps, ...props }) => (
                                    <div
                                    {...props}
                                    style={{
                                        backgroundColor: 'rgba(255, 100, 100, 0.85)',
                                        padding: '2px 10px',
                                        color: 'white',
                                        borderRadius: 3,
                                        ...props.style,
                                    }}
                                    >
                                    Simple tooltip
                                    </div>
                                )}
                            </Overlay>
                            <td>{item.name}</td>
                            <td>$ {item.unitPrice}</td>
                            <td>{item.quantity}</td>
                            <td>
                                {item.quantity === 0 ? (
                                <Badge pill variant="dark">Out of Stock</Badge> 
                                ) : (
                                <Badge pill variant="warning">Available</Badge>
                                )}
                            </td>
                        </tr> 
                     </tbody>
                    ))}
                </Table>
            </Row>
        </Container>  
      </div>
    )
  }

}


export default withRouter(AllItems);
