import React, { Component } from 'react';
import '../App.css'

class LoginPage extends Component {

    constructor() {
        super();
        this.state = {
            username: '',
            password: '',
            error: ''
        };

        this.handleUsernameChange = this.handleUsernameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.dismissError = this.dismissError.bind(this);
    }

    handleUsernameChange(e) {
        this.setState({
            username: e.target.value
        });
    }

    handlePasswordChange(e) {
        this.setState({
            password: e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();

        if (!(this.state.username || this.state.password)) {
            return this.setState({ error: 'Username and Password is required' });
        }
        if (!this.state.username) {
            return this.setState({ error: 'Username is required' });
        }

        if (!this.state.password) {
            return this.setState({ error: 'Password is required' });
        }

        // return this.setState({ error: '' });
        // this.props.history.push('/orders');
    }

    dismissError() {
        this.setState({ error: '' });
    }

    render() {
      return (
        <div className="login-form">
            <h1>Login</h1>
            <form onSubmit={this.handleSubmit} >

            {
                this.state.error &&
                <h4 type="error" onClick={this.dismissError}>
                <button onClick={this.dismissError}>âœ–</button>
                {" " + this.state.error}
                </h4>
            }

            <label>Username</label>
            <input type="text" name="username" placeholder="Username" value={this.state.username} onChange={this.handleUsernameChange}/>

            <label>Password</label>
            <input type="password" name="password" placeholder="Password" value={this.state.password} onChange={this.handlePasswordChange}/>

            <input type="submit" value="Login"/>
          </form>
        </div>
      );
    }
  }

  export default LoginPage;
