import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Button } from 'react-bootstrap';
import NumericUpDown from 'bootstrap-react-numeric-up-down';

class ItemList extends Component {

  render() {
    return (
      <div>
         <Container>
          <Row>
            <Table responsive striped>
              <thead>
                <tr>
                  <th>Item Name</th>
                  <th>Unit Price</th>
                  <th>Quantity</th>
                  <th>Net Amount</th>
                  <th></th>
                </tr>
              </thead>

              {this.props.items.map((item) => (
                <tbody key={item._id}> 
                  <tr>
                    <td>{item.id.name}</td>
                    <td>{item.id.unitPrice}</td>
                    {/* <td>{item.quantity}</td> */}
                    <td><NumericUpDown value={item.quantity} min={0} max={item.id.quantity} step={1} /></td>
                    <td>{item.id.unitPrice*item.quantity}</td>
                    <td><Button variant="outline-info">Add Items</Button></td>
                </tr>
                </tbody>
              ))}

            </Table>
          </Row>
        </Container>
      </div>
    )
  }

}

ItemList.propTypes = {
  items: PropTypes.array.isRequired
}

export default withRouter(ItemList);
