import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Button, ButtonGroup } from 'react-bootstrap';
import 'font-awesome/css/font-awesome.min.css'

class ItemList extends Component {

  render() {
    return (
      <div>
         <Container>
          <Row>
            <Table responsive striped>
              <thead>
                <tr>
                  <th>Item Name</th>
                  <th>Unit Price</th>
                  <th>Quantity</th>
                  <th>Net Amount</th>
                  <th></th>
                </tr>
              </thead>

              {this.props.items.map((item) => (
                <tbody key={item._id}> 
                  <tr>
                    <td>{item.id.name}</td>
                    <td>{item.id.unitPrice}</td>
                    {/* <td>{item.quantity}</td> */}
                    <td>
                        <ButtonGroup className="mr-2"  aria-label="First group">
                            <Button variant="secondary">-</Button>
                            <Button variant="outline-secondary" disabled>{item.quantity}</Button>
                            <Button variant="secondary">+</Button>
                        </ButtonGroup>
                    </td>
                    <td>{item.id.unitPrice*item.quantity}</td>
                    <td><Button variant="outline-danger"><i className="fa fa-sign-trash"></i></Button>   </td>
                </tr>
                </tbody>
              ))}

            </Table>
          </Row>
        </Container>
      </div>
    )
  }

}

ItemList.propTypes = {
  items: PropTypes.array.isRequired
}

export default withRouter(ItemList);
