import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Col, Card, Table, Badge } from 'react-bootstrap';
import axios from 'axios';
import NavBar from './NavBar'
// import Items from './Items';
// import OrderedItems from './OrderedItems';
import '../App.css'


class AddItems extends Component {

    constructor(props) {
        super(props)

        this.state = {
            orderId: this.props.match.params.OrderId,
            allItems: [],
            itemDetails: '',
            addedItems: this.props.location.state.items
        }
    }

    componentDidMount () {
        var url = "http://localhost:3000/allItems";
        axios.get(url)
        .then(response => {
            if(response.status === 200){
                console.log(response.data)
                this.setState({ allItems: response.data})
            }
        })
        .catch(error => {
            console.log(error);
        })
    }

    handleRowClick(event, itemId,availableQuantity) {

        if(availableQuantity !== 0){
            var url = "http://localhost:3000/item/"+itemId;
            axios.get(url)
            .then(response => {
                if(response.status === 200){
                    console.log(response.data)

                    const item = {
                        _id: response.data._id,
                        name: "",
                        quantity: 0
                    };

                    this.setState({
                        addedItems: [...this.state.addedItems, item]
                    });
                }
            })
            .catch(error => {
                console.log(error);
            })
        }

    }


    render() {

        return (
        <div className="order-items">
            <NavBar/>
            <Container fluid='true'>
                <Row>
                    <Col md={4}>
                        <Card>
                            <Card.Header>Item List</Card.Header>
                            <Card.Body>
                                <Table responsive size="sm" hover>
                                    <thead>
                                        <tr>
                                            <th>Item</th>
                                            <th>Unit Price</th>
                                            <th></th>
                                        </tr>
                                    </thead>

                                    {this.state.allItems.map((item) => (
                                        <tbody  key={item._id}> 
                                            <tr onClick={(event) => this.handleRowClick(event,item._id,item.quantity)}>
                                                <td>{item.name}</td>
                                                <td>{item.unitPrice}</td>
                                                <td>
                                                    {item.quantity === 0 ? (
                                                    <Badge pill variant="dark">Out of Stock</Badge>
                                                    ) : (
                                                    <Badge pill variant="warning">Available</Badge>
                                                    )}
                                                </td>
                                            </tr> 
                                        </tbody>
                                    ))}
                                </Table>
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col md={8}>
                        <Card>
                            <Card.Body>

                                    <Table responsive size="sm" hover>
                                        <thead>
                                            <tr>
                                                <th>Item Name</th>
                                                <th>Quantity</th>
                                                <th>Sub Total</th>
                                            </tr>
                                        </thead>
                                            {this.state.addedItems.map((item) => (
                                                <tbody key={item._id}> 
                                                    <tr>
                                                        {item.id.name ? <td>{item.id.name}</td> : <td>{item.name}</td>}

                                                        <td>{item.quantity}</td>
                                                        {/* <td>{item.quantity*item.id.unitPrice}</td> */}
                                                    </tr> 
                                                </tbody>
                                            ))}
                                    </Table>

                            </Card.Body>
                        </Card>
                    </Col>
                </Row>



            </Container>
        </div>
        );  
    }
}

export default withRouter(AddItems);
