import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';

class AddItems extends Component {

    constructor(props){
        super(props)

        this.state = {
            items: []
        }
    }

    componentDidMount () {
        var url = "http://localhost:3000/allItems";

        axios.get(url)
        .then(response => {
            if(response.status === 200){
                this.setState({ items: response.data})
                console.log(this.state.items)
            }
        })
        .catch(error => {
            console.log(error);
        })

      }

    handleAddItems(event) {
        this.props.onHide();
    }

    render() {
        return (
            <Modal {...this.props} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">Add Items</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Items items={this.state.items}/>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={this.props.onHide}>Cancel</Button>
              <Button variant="outline-success" onClick={event => this.handleAddItems(event)}>Add</Button>
            </Modal.Footer>
            </Modal>
        );
    }
}

export default withRouter(AddItems);
