import React, { Component } from 'react';
import '../App.css'

class LoginPage extends Component {

    constructor() {
        super();
        this.state = {
            username: '',
            password: '',
            error: ''
        };

        this.handleUsernameChange = this.handleUsernameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
    }

    handleUsernameChange(e) {
        this.setState({
            username: e.target.value,
        });
    }

    handlePasswordChange(e) {
        this.setState({
            password: e.target.value,
        });
    }

    handleSubmit(e) {

    }

    render() {
      return (
        <div className="login-form">
            <h1>Login</h1>
          <form onSubmit={this.handleSubmit} >
            <label>Username</label>
            <input type="text" name="username" placeholder="Username" value={this.state.username} onChange={this.handleUsernameChange} required />

            <label>Password</label>
            <input type="password" name="password" placeholder="Password" value={this.state.password} onChange={this.handlePasswordChange} required />

            <input type="submit" value="Login"/>
          </form>
        </div>
      );
    }
  }

  export default LoginPage;
