import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Badge } from 'react-bootstrap';

class Items extends Component {

    handleClick(event,itemQuantity) {
        // console.log(itemQuantity)
        if(itemQuantity !== 0){
            this.props.callbackFromAddItems(itemQuantity);
        }
    }

    render() {

        return (
        <div className="all-items">
            <Container>
            <Row>
                <Table responsive size="sm" hover>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Unit Price</th>
                        <th></th>
                    </tr>
                </thead>

                {this.props.allItems.map((item) => (
                    <tbody  key={item._id}> 
                        <tr onClick={(event) => this.handleClick(event,item.quantity)}>
                            <td>{item.name}</td>
                            <td>{item.unitPrice}</td>
                            <td>
                                {item.quantity === 0 ? (
                                <Badge pill variant="dark">Out of Stock</Badge>
                                ) : (
                                <Badge pill variant="warning">Available</Badge>
                                )}
                            </td>
                        </tr> 
                    </tbody>
                ))}

                </Table>
            </Row>
            </Container> 
        </div>
        )
    }

}

Items.propTypes = {
  allItems: PropTypes.array.isRequired
}

export default withRouter(Items);
