import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Badge } from 'react-bootstrap';
import moment from 'moment';

class OrderList extends Component {

  // constructor (){
  //   super()

  //   this.showOrderDetails = this.showOrderDetails.bind(this);
  // }

  // showOrderDetails(e){
  //   console.log(e.variant)
  // }

  render() {
    return (
      <div>
         <Container>
          <Row>
            <Table responsive>
              <thead>
                <tr>
                  <th>Order Number</th>
                  <th>Status</th>
                  <th>Created Time</th>
                  <th>Total Price</th>
                </tr>
              </thead>

              {this.props.orders.map((order) => (
                <tbody> 
                  <tr>
                  <td>{order._id}</td>
                  <td>
                    {order.status === "open" ? (
                      <Badge variant="success">{order.status}</Badge>
                        ) : (
                      <Badge variant="danger">{order.status}</Badge>
                    )}
                  </td>
                  <td>{moment(order.createdAt).format("DD-MM-YYYY h:mm:ss")}</td>
                  <td>price here</td>
                </tr>
                </tbody>
              ))}

            </Table>
          </Row>
        </Container>
      </div>
    )
  }

}

OrderList.propTypes = {
  orders: PropTypes.array.isRequired
}

export default withRouter(OrderList);
