import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Table, Badge } from 'react-bootstrap';
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css'

class AllItems extends Component {

  constructor(props) {
    super(props)

    this.state = {
        orderId: this.props.orderId,
        allItems: []
    }
  }

  componentDidMount () {
    var url = "http://localhost:3000/allItems";
    axios.get(url)
    .then(response => {
        if(response.status === 200){
            console.log(response.data)
            this.setState({ allItems: response.data})
        }
    })
    .catch(error => {
        console.log(error);
    })
}

  render() {

    return (
      <div>
         <Container>
          <Row>
          <Table responsive size="sm" hover>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Unit Price</th>
                        <th></th>
                    </tr>
                </thead>

                {this.state.allItems.map((item) => (
                    <tbody key={item._id}> 
                        <tr onClick={(event) => this.handleRowClick(event,item)}>
                            <td>{item.name}</td>
                            <td>{item.unitPrice}</td>
                            <td>
                                {item.quantity === 0 ? (
                                <Badge pill variant="dark">Out of Stock</Badge>
                                ) : (
                                <Badge pill variant="warning">Available</Badge>
                                )}
                            </td>
                        </tr> 
                    </tbody>
                ))}
            </Table>
          </Row>
        </Container>  
      </div>
    )
  }

}


export default withRouter(AllItems);
