import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Button, ButtonGroup } from 'react-bootstrap';
import 'font-awesome/css/font-awesome.min.css'
import DeleteModal from './DeleteModal';

class ItemList extends Component {

  constructor(props) {
    super(props)

    this.state = {
        modalShow: false,
        orderId: this.props.orderId,
        itemId: null
    }
}

handleDeleteItem (event, id){

  this.setState({
    itemId: id,
    modalShow: true
  })
}

  render() {

    let modalClose = () => this.setState({ modalShow: false });

    return (
      <div>
         <Container>
          <Row>
            <Table responsive striped>
              <thead>
                <tr>
                  <th>Item Name</th>
                  <th>Unit Price</th>
                  <th>Quantity</th>
                  <th>Sub Total</th>
                  <th></th>
                </tr>
              </thead>

              {this.props.items.map((item) => (
                <tbody key={item._id}> 
                    <tr>
                        <td>{item.id.name}</td>
                        <td>{item.id.unitPrice}</td>
                        <td>
                            <ButtonGroup className="mr-2"  aria-label="First group">
                                <Button variant="secondary">-</Button>
                                <Button variant="outline-secondary" disabled>{item.quantity}</Button>
                                <Button variant="secondary">+</Button>
                            </ButtonGroup>
                        </td>
                        <td>{item.id.unitPrice*item.quantity}</td>
                        <td>
                            <Button variant="outline-danger" onClick={(event) => this.handleDeleteItem(event, '123')}><i className="fa fa-trash"></i></Button>
                        </td>
                    </tr>
                </tbody>
              ))}

            </Table>
          </Row>
        </Container>

        <DeleteModal show={this.state.modalShow} onHide={modalClose} orderId={this.state.orderId} itemId={this.state.itemId}/>
      </div>
    )
  }

}

ItemList.propTypes = {
  items: PropTypes.array.isRequired
}

export default withRouter(ItemList);
