import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from 'axios'
import NavBar from './NavBar'
import '../App.css'
import ItemList from './ItemList';

class OrderItems extends Component {

    constructor(props) {
        super(props)

        this.state = {
            orderId: this.props.match.params.OrderId,
            items : []
        }
    }

    componentDidMount () {

        var url = "http://localhost:3000/itemsByOrderId/"+this.state.orderId;
        // var url = "http://localhost:3000/order/"+this.state.orderId;

        axios.get(url)
            .then(response => {
                if(response.status === 200){
                    console.log(response.data)
                    this.setState({ items: response.data.items})
                    console.log(this.state.items)
                }
            })
            .catch(error => {
                console.log(error);
            })

      }

    render() {
        return (
        <div className="order-items">
            <NavBar/>
            <Container>
                <Row>
                    <Col>
                        <Card>
                            <Card.Header>
                                <Button className="pull-right" variant="outline-success">Add Items</Button>
                            </Card.Header>
                            <Card.Body>
                                <ItemList items={this.state.items}/>
                            </Card.Body>
                            <Card.Footer>
                                <Row><h6 style={{textAlign:"center"}}>Number of items: {this.state.items.length}</h6><hr/></Row>
                                <Row><h5>Total Price: $34</h5></Row>
                            </Card.Footer>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
        );  
    }
}

export default withRouter(OrderItems);
