import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Button, ButtonGroup } from 'react-bootstrap';
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css'
import DeleteModal from './DeleteModal';

class ItemList extends Component {

  constructor(props) {
    super(props)

    this.state = {
      modalShow: false,
      orderId: this.props.orderId,
    }

    this.removeItem = this.removeItem.bind(this)
  }

  removeItem(items){
    this.props.handleChanges(items)
  }

  handleValueDown(itemId, itemQuantity){
    console.log(itemQuantity)

    if(itemQuantity !== 1){
        var url = "http://localhost:3000/changeItemQuantity/"+this.state.orderId+"/"+itemId;

        axios.put(url,{
            quantity: itemQuantity-1
        })
        .then(response => {
            if(response.status === 200){
                console.log(response.data)
                this.props.handleChanges(response.data.items)
            }
        })
        .catch(error => {
            console.log(error);
        })
      }
  }

  handleValueUp(itemId, itemRefId, itemQuantity){

    // if(itemQuantity !== 1){
      var url = "http://localhost:3000/incrementItemQuantity/"+this.state.orderId+"/"+itemId;

      axios.put(url,{
          refId: itemRefId,
          quantity: itemQuantity+1
      })
      .then(response => {
          if(response.status === 200){
              // console.log("item: ",response[0])
              this.props.handleChanges(response.data[0].items)
              // this.props.handleChanges(response.data)
          }
      })
      .catch(error => {
          console.log(error);
      })
    // }

  }

  render() {

    let modalClose = () => this.setState({ modalShow: false });

    return (
      <div>
         <Container>
          <Row>
            <Table responsive striped>
              <thead>
                <tr>
                  <th>Item Name</th>
                  <th>Quantity</th>
                  <th>Sub Total</th>
                  <th></th>
                </tr>
              </thead>

              {this.props.items.map((item) => (
                <tbody key={item.id._id}> 
                    <tr>
                        <td>{item.id.name}</td>
                        <td>
                            <ButtonGroup className="mr-2"  aria-label="First group">
                                <Button variant="secondary" onClick={event => this.handleValueDown(item._id, item.quantity)}>-</Button>
                                <Button variant="outline-secondary" disabled>{item.quantity}</Button>
                                <Button variant="secondary" onClick={event => this.handleValueUp(item._id, item.id._id, item.quantity)}>+</Button>
                            </ButtonGroup>
                        </td>
                        <td>$ {item.id.unitPrice*item.quantity}</td>
                        <td> 
                            <Button variant="outline-danger" onClick={() => this.setState({ modalShow: true})}><i className="fa fa-trash"></i></Button>
                            <DeleteModal show={this.state.modalShow} onHide={modalClose} orderId={this.state.orderId} itemId={item._id} removeItem={this.removeItem}/>
                        </td>
                    </tr> 
                </tbody>
              ))}

            </Table>
          </Row>
        </Container>  
      </div>
    )
  }

}

ItemList.propTypes = {
  items: PropTypes.array.isRequired
}

export default withRouter(ItemList);
