import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Badge, Table } from 'react-bootstrap';
import axios from 'axios'
import NavBar from './NavBar'
import '../App.css'
import ItemList from './ItemList';

class OrderItems extends Component {

    constructor(props) {
        super(props)

        this.state = {
            orderId: this.props.match.params.OrderId,
            items : [],
            allItems: []
        }
    }

    componentDidMount () {

        var url1 = "http://localhost:3000/itemsByOrderId/"+this.state.orderId;
        axios.get(url1)
        .then(response => {
            if(response.status === 200){
                console.log(response.data)
                this.setState({ items: response.data.items})
                console.log(this.state.items)
            }
        })
        .catch(error => {
            console.log(error);
        })

        var url2 = "http://localhost:3000/allItems";
        axios.get(url2)
        .then(response => {
            if(response.status === 200){
                console.log(response.data)
                this.setState({ allItems: response.data})
            }
        })
        .catch(error => {
            console.log(error);
        })
    }


    handleClick(event) {
        this.props.history.push("/MainPage")
    }

    handleRowClick(event, item) {

            if(
                (item.quantity !== 0) &&
                (!(this.state.items.some(element => item._id === element.id._id)))
            ){
                console.log(item.name)

                // const newItem = {
                //     id: {
                //         _id:item._id,
                //         name: item.name,
                //         unitPrice: item.unitPrice
                //     },
                //     quantity: 0

                // };

                var url = "http://localhost:3000/addNewItem/"+this.state.orderId;

                const data = {
                    id: item._id,
                    quantity: 0
                }
                axios.put(url,data)
                .then(response => {
                    if(response.status === 200){
                        console.log(response.data)
                        // this.setState({ allItems: response.data})
                    }
                })
                .catch(error => {
                    console.log(error);
                })

                // this.setState({
                //     items: [...this.state.items, newItem]
                // });
        }

    }

    render() {
        return (
        <div className="order-items">
            <NavBar/>
            <Container fluid='true'>
                <Row>


                <Col md={4}>
                        <Card>
                            <Card.Header>Item List</Card.Header>
                            <Card.Body>
                                <Table responsive size="sm" hover>
                                    <thead>
                                        <tr>
                                            <th>Item</th>
                                            <th>Unit Price</th>
                                            <th></th>
                                        </tr>
                                    </thead>

                                    {this.state.allItems.map((item) => (
                                        <tbody key={item._id}> 
                                            <tr onClick={(event) => this.handleRowClick(event,item)}>
                                                <td>{item.name}</td>
                                                <td>{item.unitPrice}</td>
                                                <td>
                                                    {item.quantity === 0 ? (
                                                    <Badge pill variant="dark">Out of Stock</Badge>
                                                    ) : (
                                                    <Badge pill variant="warning">Available</Badge>
                                                    )}
                                                </td>
                                            </tr> 
                                        </tbody>
                                    ))}
                                </Table>
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col md={8}>
                        <Card>
                            <Card.Header>
                                <Button variant="outline-primary" onClick={(event) => this.handleClick(event)}><i className="fa fa-chevron-left"></i></Button>
                            </Card.Header>
                            <Card.Body>
                                <ItemList items={this.state.items} orderId={this.state.orderId}/>
                            </Card.Body>
                            <Card.Footer>
                                <h5 style={{color: '#5cb85c', textAlign:'right'}}>Total Price: $34</h5>
                            </Card.Footer>
                        </Card>
                    </Col>
                </Row>
            </Container>

        </div>
        );  
    }
}

export default withRouter(OrderItems);
