import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Container, Row, Col, Card } from 'react-bootstrap';
import axios from 'axios';
import NavBar from './NavBar'
import Items from './Items';
import OrderedItems from './OrderedItems';
import '../App.css'


class AddItems extends Component {

    constructor(props) {
        super(props)

        this.state = {
            orderId: this.props.match.params.OrderId,
            allItems: [],
            addedItems : [{}]
        }
    }

    componentDidMount () {
        var url = "http://localhost:3000/allItems";
        axios.get(url)
        .then(response => {
            if(response.status === 200){
                console.log(response.data)
                this.setState({ allItems: response.data})
            }
        })
        .catch(error => {
            console.log(error);
        })
    }

    itemCallback = (itemId) => {

        console.log(itemId)

        const item = {
            id: itemId
        };

        this.setState({
            addedItems: [...this.state.addedItems, item]
        });

        console.log(this.addedItems.length)

    }

    render() {

        return (
        <div className="order-items">
            <NavBar/>
            <Container fluid='true'>
                <Row>
                    <Col md={4}>
                        <Card>
                            <Card.Header>Item List</Card.Header>
                            <Card.Body>
                                <Items allItems={this.state.allItems} callbackFromAddItems={this.itemCallback}/>
                            </Card.Body>
                        </Card>
                    </Col>

                    <Col md={8}>
                        <Card>
                            <Card.Body>
                                {
                                    this.state.addedItems.length === 0 &&
                                    <OrderedItems items={this.state.addedItems}/>
                                }

                            </Card.Body>
                        </Card>
                    </Col>
                </Row>



            </Container>
        </div>
        );  
    }
}

export default withRouter(AddItems);
