import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Container, Row, Table, Button, ButtonGroup } from 'react-bootstrap';
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css'
import DeleteModal from './DeleteModal';

class ItemList extends Component {

  constructor(props) {
    super(props)

    this.state = {
        modalShow: false,
        orderId: this.props.orderId,
        items: []
    }
  }

  componentDidMount () {
    var url = "http://localhost:3000/itemsByOrderId/"+this.state.orderId;
    axios.get(url)
    .then(response => {
        if(response.status === 200){
            // console.log(response.data)
            this.setState({ items: response.data.items})
            // console.log(this.state.items)
        }
    })
    .catch(error => {
        console.log(error);
    })
  }


  render() {

    let modalClose = () => this.setState({ modalShow: false });

    return (
      <div>
         <Container>
          <Row>
            <Table responsive striped>
              <thead>
                <tr>
                  <th>Item Name</th>
                  <th>Quantity</th>
                  <th>Sub Total</th>
                  <th></th>
                </tr>
              </thead>

              {this.props.items.map((item) => (
                <tbody key={item.id._id}> 
                    <tr>
                        <td>{item.id.name}</td>
                        <td>
                            <ButtonGroup className="mr-2"  aria-label="First group">
                                <Button variant="secondary">-</Button>
                                <Button variant="outline-secondary" disabled>{item.quantity}</Button>
                                <Button variant="secondary">+</Button>
                            </ButtonGroup>
                        </td>
                        <td>{item.id.unitPrice*item.quantity}</td>
                        <td> 
                            <Button variant="outline-danger" onClick={() => this.setState({ modalShow: true})}><i className="fa fa-trash"></i></Button>
                            <DeleteModal show={this.state.modalShow} onHide={modalClose} orderId={this.state.orderId} itemId={item._id} />
                        </td>
                    </tr> 
                </tbody>
              ))}

            </Table>
          </Row>
        </Container>


      </div>
    )
  }

}

// ItemList.propTypes = {
//   items: PropTypes.array.isRequired
// }

export default withRouter(ItemList);
